mapper.select:
select a.shum,a.gjdj,a.tsfljc,b.xscs from FxsgkView a left join (select shum,dj,tsfljc, sum(cs) as xscs from (select fhdbh,sxh,shum,tsfljc, dj,kwbh,cs from xsmxview union select fhdbh,sxh,shum,tsfljc,dj,kwbh,cs from XsmxnView) xsmx where fhdbh in (select fhdbh from (select fhdbh,convert(varchar(7),dateadd(month,0,shrq),120) shrq,khbh,fhzt,xsbmmc from xsdview union select fhdbh,convert(varchar(7),dateadd(month,0,shrq),120) shrq,khbh,fhzt,xsbmmc from xsdnview) xsd
#{fhzt}
and shrq >= #{startdate} and shrq <= #{enddate}
and xsbmmc like '%${xsbmmc}%'
and khbh <> #{khbhRemove}) group by shum,dj,tsfljc) b on a.shum = b.shum and a.tsfljc = b.tsfljc and a.gjdj = b.dj where sxh not like #{sxhRemove}
#{tsflmc}
and bmmc like '%${bjbmmc}%' and a.tsfljc like '%${tsfljc}%' group by a.shum,a.gjdj, a.tsfljc,b.xscs order by b.xscs desc

select shum,tsfljc,dj as gjdj,kjqj,SUM(qckc) qckc,SUM(qmkc) qmkc from (SELECT shum,tsfljc,dj,qckc,qmkc,kjqj,sxh,kwbh
FROM kcknView union select shum,tsfljc,dj,qckc,qmkc,convert(varchar(7),dateadd(month,0,getdate()),120)
kjqj,sxh,kwbh from KucunView) t1

    KWBH = #{kwbh} AND kjqj >= #{kjqj}

        #{tsflmc}

    and sxh not like #{sxhRemove} group by shum,tsfljc,dj,kjqj
